    ItemCreateSchema(name="Belt of Giant Strength (Hill)", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=1.0, cost_gp=0, properties={"rarity": "rare", "attunement": True, "effect": "While wearing this belt, your Strength score changes to 21."}),
