    ItemCreateSchema(name="Periapt of Wound Closure", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=1.0, cost_gp=0, properties={"rarity": "uncommon", "attunement": True, "effect": "While you wear this pendant, you stabilize whenever you are dying at the start of your turn. In addition, whenever you roll a Hit Die to regain hit points, double the number of hit points it restores."}),
    ItemCreateSchema(name="Portable Hole", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=0.0, cost_gp=0, properties={"rarity": "rare", "effect": "This fine black cloth, soft as silk, is folded up to the dimensions of a handkerchief. It unfolds into a circular sheet 6 feet in diameter. You can use an action to unfold a portable hole and place it on or against a solid surface, whereupon the portable hole creates an extradimensional hole 10 feet deep."}),
    ItemCreateSchema(name="Rope of Climbing", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=3.0, cost_gp=0, properties={"rarity": "uncommon", "effect": "This 60-foot length of silk rope weighs 3 pounds and can hold up to 3,000 pounds. If you hold one end of the rope and use an action to speak the command word, the rope animates."}),
