    ItemCreateSchema(name="Stone of Good Luck (Luckstone)", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=0.5, cost_gp=0, properties={"rarity": "uncommon", "attunement": True, "effect": "While this polished agate is on your person, you gain a +1 bonus to ability checks and saving throws."}),
    ItemCreateSchema(name="Wind Fan", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=1.0, cost_gp=0, properties={"rarity": "uncommon", "effect": "While holding this fan, you can use an action to cast the gust of wind spell (save DC 13) from it. Once used, the fan can't be used again until the next dawn."}),
    ItemCreateSchema(name="Winged Boots", item_type=ItemTypeEnum.WONDROUS_ITEM, weight=1.0, cost_gp=0, properties={"rarity": "uncommon", "attunement": True, "effect": "While you wear these boots, you have a flying speed equal to your walking speed. You can use the boots to fly for up to 4 hours, all at once or in several shorter flights, each one using a minimum of 1 minute from the duration."}),
    
