    ItemCreateSchema(name="Plate Armor of Etherealness", item_type=ItemTypeEnum.ARMOR, weight=65.0, cost_gp=0, properties={"rarity": "legendary", "attunement": True, "effect": "While you're wearing this armor, you can speak its command word as an action to gain the effect of the etherealness spell, which lasts for 10 minutes or until you remove the armor or use an action to speak the command word again."}),

