    # Level 5 Spells
    SpellCreateSchema(name="Animate Objects", description="Objects come to life at your command. Choose up to ten nonmagical objects within range that are not being worn or carried.", higher_level="If you cast this spell using a spell slot of 6th level or higher, you can animate two additional objects for each slot level above 5th.", range="120 feet", components="V, S", material=None, ritual=False, duration="Concentration, up to 1 minute", concentration=True, casting_time="1 action", level=5, school=SchoolOfMagicEnum.TRANSMUTATION, dnd_classes=["Bard", "Sorcerer", "Wizard"], source_book="SRD"),
