    SpellCreateSchema(name="Teleportation Circle", description="As you cast the spell, you draw a 10-foot-diameter circle on the ground inscribed with sigils that link your location to a permanent teleportation circle of your choice whose sigil sequence you know and that is on the same plane of existence as you.", higher_level=None, range="10 feet", components="V, M", material="rare chalks and inks infused with precious gems worth 50 gp, which the spell consumes", ritual=False, duration="1 round", concentration=False, casting_time="1 minute", level=5, school=SchoolOfMagicEnum.CONJURATION, dnd_classes=["Bard", "Sorcerer", "Wizard"], source_book="SRD"),
