    SpellCreateSchema(name="Blade Barrier", description="You create a vertical wall of whirling, razor-sharp blades made of magical energy. The wall appears within range and lasts for the duration. You can make a straight wall up to 100 feet long, 20 feet high, and 5 feet thick, or a ringed wall up to 60 feet in diameter, 20 feet high, and 5 feet thick.", higher_level=None, range="90 feet", components="V, S", material=None, ritual=False, duration="Concentration, up to 10 minutes", concentration=True, casting_time="1 action", level=6, school=SchoolOfMagicEnum.EVOCATION, dnd_classes=["Cleric"], source_book="SRD"),
