    SpellCreateSchema(name="Disintegrate", description="A thin, green ray springs from your pointing finger to a target that you can see within range. The target can be a creature, an object, or a creation of magical force, such as the wall created by wall of force. A creature targeted by this spell must make a Dexterity saving throw. On a failed save, the target takes 10d6 + 40 force damage.", higher_level="When you cast this spell using a spell slot of 7th level or higher, the damage increases by 3d6 for each slot level above 6th.", range="60 feet", components="V, S, M", material="a lodestone and a pinch of dust", ritual=False, duration="Instantaneous", concentration=False, casting_time="1 action", level=6, school=SchoolOfMagicEnum.TRANSMUTATION, dnd_classes=["Sorcerer", "Wizard"], source_book="SRD"),
