    SpellCreateSchema(name="Sunbeam", description="A beam of brilliant light flashes out from your hand in a 5-foot-wide, 60-foot-long line. Each creature in the line must make a Constitution saving throw. On a failed save, a creature takes 6d8 radiant damage and is blinded until your next turn. On a successful save, it takes half as much damage and isn't blinded by this spell.", higher_level="When you cast this spell using a spell slot of 7th level or higher, the damage increases by 1d8 for each slot level above 6th.", range="Self (60-foot line)", components="V, S, M", material="a magnifying glass", ritual=False, duration="Concentration, up to 1 minute", concentration=True, casting_time="1 action", level=6, school=SchoolOfMagicEnum.EVOCATION, dnd_classes=["Druid", "Sorcerer", "Wizard"], source_book="SRD"),
