    SpellCreateSchema(name="Finger of Death", description="You send negative energy coursing through a creature that you can see within range, causing it searing pain. The target must make a Constitution saving throw. It takes 7d8 + 30 necrotic damage on a failed save, or half as much damage on a successful one.", higher_level=None, range="60 feet", components="V, S", material=None, ritual=False, duration="Instantaneous", concentration=False, casting_time="1 action", level=7, school=SchoolOfMagicEnum.NECROMANCY, dnd_classes=["Sorcerer", "Warlock", "Wizard"], source_book="SRD"),
    SpellCreateSchema(name="Plane Shift", description="You and up to eight willing creatures who link hands in a circle are transported to a different plane of existence. You can specify a target destination in general terms, such as the City of Brass on the Elemental Plane of Fire or the palace of Dispater on the second level of the Nine Hells, and you appear in or near that destination.", higher_level=None, range="Touch", components="V, S, M", material="a forked, metal rod worth at least 250 gp, attuned to a particular plane of existence", ritual=False, duration="Instantaneous", concentration=False, casting_time="1 action", level=7, school=SchoolOfMagicEnum.CONJURATION, dnd_classes=["Cleric", "Druid", "Sorcerer", "Warlock", "Wizard"], source_book="SRD"),
